mapper.select:
SELECT COUNT(*)
FROM
information_schema.tables
WHERE table_schema = #{schema}
AND table_name
= #{tableName}

		select
		
		log_no, detected_dt, src_ip, src_port, target_ip, target_port,
		protocol, threat_level, p_id, action, payload, policy_type

		from
		${tableName}	

		
			
			
				detected_dt >= #{startDate}
			
			
				AND detected_dt <= #{endDate}
			

			
			
				AND src_ip = #{srcIp}
			

			
			
				AND inet_aton(src_ip) between inet_aton(#{srcIpStart})
				AND
				inet_aton(#{srcIpEnd})
			

			
			
				AND target_ip = #{targetIp}
			

			
			
				AND inet_aton(target_ip) between
				inet_aton(#{targetIpStart}) AND
				inet_aton(#{targetIpEnd})
			

			
			
				AND protocol IN
				
					#{item}
				
			

			
			
				AND threat_level IN
				
					#{item}
				
			

			
			
				AND p_id = #{pId}
			

			
			
				AND p_id IN
				
					#{item}
				
			

			
				AND action IN
				
					#{act}
				
			

			
			
			

			
			
				AND policy_type = #{policyType}
			
		
		order by detected_dt
		
			
				ASC
			
			
				DESC
			
		

		
		
			LIMIT #{limit} OFFSET #{offset}

		select count(*)
		from ${tableName}
		
			
			
			
				detected_dt >= #{startDate}
			
			
				AND detected_dt <= #{endDate}
			

			
			
				AND src_ip = #{srcIp}
			

			
			
				AND inet_aton(src_ip) between inet_aton(#{srcIpStart})
				AND
				inet_aton(#{srcIpEnd})
			

			
			
				AND target_ip = #{targetIp}
			

			
			
				AND inet_aton(target_ip) between
				inet_aton(#{targetIpStart}) AND
				inet_aton(#{targetIpEnd})
			

			
			
				AND protocol IN
				
					#{item}
				
			

			
			
				AND threat_level IN
				
					#{item}
				
			

			
			
				AND p_id = #{pId}
			

			
			
				AND p_id IN
				
					#{item}
				
			

			
				AND action IN
				
					#{act}
				
			

			
			
			

			
			
				AND policy_type = #{policyType}
